<?php//// Базовый контроллер сайта.//abstract class C_Base extends C_Controller{	protected $title; // заголовок страницы    protected $menu; // массив элементов верхнего меню страницы	protected $content; // содержание страницы    protected $logged_user; // пользователь блога (если null, пользователь не залогинен)    protected $error_msg; // сообщение об ошибке    protected $blocks; //блоки правой колонки шаблона	//	// Конструктор.	//	function __construct()	{			}		protected function before()	{		$this->title = 'Блоги';        $this->menu = array();		$this->content = '';        $this->logged_user = M_Users::instance()->autoLogin();        $this->error_msg = '';        $this->blocks =array();	}		//	// Генерация базового шаблонаы	//		public function render($base_template)	{		$vars = array('logged_user' => $this->logged_user, 'title' => $this->title, 'menu' => $this->menu, 'content' => $this->content, 'blocks' => $this->blocks, 'error_msg' => $this->error_msg);		$page = $this->template($base_template, $vars);		echo $page;	}	}