<?phprequire_once('startup.php');function __autoload($classname){    $dir = 'c';    if (mb_substr($classname, 0 , 2, 'UTF-8') == 'M_'){        $dir = 'm';    }    require_once("$dir/$classname.php");}$action = 'action_';$action .= (isset($_REQUEST['act'])) ? $_REQUEST['act'] : 'index';$controller = (isset($_REQUEST['c'])) ? $_REQUEST['c'] : 'articles';$base_template = (isset($_REQUEST['t'])) ? $_REQUEST['t'] : 'main';$base_template = 'v/v_'.$base_template.'.php';switch ($controller){	case 'articles':		$controller = new C_Articles();		break;    case 'users':        $controller = new C_Users();        break;    default:		$controller = new C_Articles();}$controller->request($action, $base_template, array());