<?php//// Менеджер статей//class M_Comments extends M_Table{    private static $instance; 	// ссылка на экземпляр класса    public static function instance($table_name = 'comments', $table_key = 'id')    {        if (self::$instance == null){            self::$instance = new self($table_name, $table_key);        }        return self::$instance;    }    private function __construct($table_name, $table_key)    {        parent::init($table_name, $table_key);    }    public function getAllRecords($id = '')    {        $article_id = (int)$id;        $query = "SELECT *				  FROM $this->table_name				  WHERE article_id = '$article_id'				  ORDER BY $this->table_key DESC";        return $this->msql->select($query);    }    // Добавить комментарий	//	public function addRecord($author, $content, $article_id)	{		// Подготовка.        $author = trim($author);		$content = trim($content);		// Проверка.		if ($author == ''){			return false;        }				// Запрос.		$obj = array();		$obj['author'] = $author;		$obj['content'] = $content;        $obj['article_id'] = $article_id;        $this->msql->insert($this->table_name, $obj);		return true;	}    public function getLastRecords($number_of_records)    {        $query = "SELECT *				  FROM $this->table_name				  ORDER BY id DESC LIMIT $number_of_records";        return $this->msql->select($query);    }}