<?php//// Менеджер статей//abstract class M_Table{	protected $msql; 				// драйвер БД    protected $table_name;    protected $table_key;		protected function init($table_name, $table_key)	{		$this->msql = M_MSQL::instance('localhost', 'root', 'root', 'blog');        $this->table_name = $table_name;        $this->table_key = $table_key;	}		//	// получить все записи из таблицы	//	public function getAllRecords($id='')	{		$query = "SELECT * 				  FROM $this->table_name				  ORDER BY $this->table_key DESC";				  		return $this->msql->select($query);	}	//	// получить запись по id	//	public function getRecord($id)	{		// Запрос.		$t = "SELECT * 			  FROM $this->table_name			  WHERE $this->table_key = '%d'";			  		$query = sprintf($t, $id);		$result = $this->msql->select($query);		return $result;	}	//	// Удалить запись	//	public function deleteRecord($id)	{		// Запрос.		$t = "$this->table_key = '%d'";		$where = sprintf($t, $id);		$this->msql->delete($this->table_name, $where);		return true;	}    public function getNumberOfRecords()    {        $query = "SELECT COUNT(*) AS number_of_records FROM $this->table_name";        $records =  $this->msql->select($query);        if (count($records) > 0){            return $records[0]['number_of_records'];        } else {            return 0;        }    }}